package ex_0326;

public class Ex1_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1 = 10, num2 = 3, num3 = 0;
		
//		사칙연산자
		num3 = num1 + num2; //13 
		num3 = num1 - num2;	//7	
		num3 = num1 * num2;	//30
		num3 = num1 / num2;	//3
		num3 = num1 % num2;	//1
		
//		대입연산자
		num1 = 10;
		num2 = 3;
		
//		복합대입연산자
		num1 += num2; //13
		num1 -= num2; //10
		num1 *= num2; //30
		num1 /= num2; //10
		num1 %= num2; //1
		
//		
		
		
	}

}
