public class Ex4Array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int[] arr = new int[4];
		System.out.println(arr);
		
		arr[0] = 100;
		arr[1] = 200;
		arr[2] = 300;
		arr[3] = 400;
		
		for (int i = 0; i < arr.length; i++) {
			System.out.println(arr[i]);
		}
	}

}
